                        data-testid="input-admin-form-email"
                      />
                    </div>

                    <div>
                      <Label htmlFor="adminPassword">Password</Label>
                      <Input
                        id="adminPassword"
                        type="password"
                        value={adminForm.password}
                        onChange={(e) => setAdminForm({...adminForm, password: e.target.value})}
                        required
                        data-testid="input-admin-form-password"
                      />
